        public static (int width, int height) GetPngDimensions(string dataUri)
        {
            byte[] b;

            var p = dataUri.Split("base64,");

            b = Convert.FromBase64String(p[1].Substring(0, 32));

            // 32 base64 characters encode the necessary 24 bytes
            return (b[18] << 8 | b[19], b[22] << 8 | b[23]);
        }


